namespace UHDM {
const BaseClass* extends::GetByVpiName(std::string_view name) const {
  if ((class_typespec_ != nullptr) && (class_typespec_->VpiName().compare(name) == 0)) {
    return class_typespec_;
  }
  if (arguments_ != nullptr) {
    for (const BaseClass *ref : *arguments_) {
      if (ref->VpiName().compare(name) == 0) return ref;
    }
  }
  return basetype_t::GetByVpiName(name);
}

std::tuple<const BaseClass*, UHDM_OBJECT_TYPE, const std::vector<const BaseClass*>*> extends::GetByVpiType(int32_t type) const {
  switch (type) {
    case vpiArgument: return std::make_tuple(nullptr, uhdmarguments, (const std::vector<const BaseClass*>*)arguments_);
    case vpiClassTypespec: return std::make_tuple(class_typespec_, static_cast<UHDM_OBJECT_TYPE>(0), nullptr);
  }
  return basetype_t::GetByVpiType(type);
}

extends::vpi_property_value_t extends::GetVpiPropertyValue(int32_t property) const {
  switch (property) {
    case vpiType: return vpi_property_value_t(VpiType());
  }
  return basetype_t::GetVpiPropertyValue(property);
}

void extends::DeepCopy(extends* clone, BaseClass* parent, CloneContext* context) const {
  [[maybe_unused]] ElaboratorContext* const elaboratorContext = clonecontext_cast<ElaboratorContext*>(context);
  elaboratorContext->m_elaborator.enterExtends(clone, nullptr);
  basetype_t::DeepCopy(clone, parent, context);
  if (auto obj = Class_typespec()) clone->Class_typespec(obj->DeepClone(clone, context));
  if (auto vec = Arguments()) {
    auto clone_vec = context->m_serializer->MakeExprVec();
    clone->Arguments(clone_vec);
    for (auto obj : *vec) {
      clone_vec->push_back(obj->DeepClone(clone, context));
    }
  }
  elaboratorContext->m_elaborator.leaveExtends(clone, nullptr);
}

extends* extends::DeepClone(BaseClass* parent, CloneContext* context) const {
  extends* const clone = context->m_serializer->MakeExtends();
  const uint32_t id = clone->UhdmId();
  *clone = *this;
  clone->UhdmId(id);
  DeepCopy(clone, parent, context);
  return clone;
}

int32_t extends::Compare(const BaseClass *other, CompareContext* context) const {
  int32_t r = 0;
  if (!context->m_visited.insert(this).second) return r;
  if ((r = basetype_t::Compare(other, context)) != 0) return r;

  const thistype_t *const lhs = this;
  const thistype_t *const rhs = (const thistype_t *)other;

  if ((r = SafeCompare(lhs->Class_typespec(), rhs->Class_typespec(), context)) != 0) return r;
  if ((r = SafeCompare(lhs, lhs->Arguments(), rhs, rhs->Arguments(), context)) != 0) return r;
  return r;
}

}  // namespace UHDM
