module_inst* module_inst::DeepClone(BaseClass* parent, CloneContext* context) const {
  module_inst* const clone = context->m_serializer->MakeModule_inst();
  const uint32_t id = clone->UhdmId();
  *clone = *this;
  clone->UhdmId(id);
  DeepCopy(clone, parent, context);
  return clone;
}

int32_t module_inst::Compare(const BaseClass *other, CompareContext* context) const {
  int32_t r = 0;
  if (!context->m_visited.insert(this).second) return r;
  if ((r = basetype_t::Compare(other, context)) != 0) return r;

  const thistype_t *const lhs = this;
  const thistype_t *const rhs = (const thistype_t *)other;

  if ((r = lhs->VpiIndex() - rhs->VpiIndex()) != 0) {
    context->m_failedLhs = lhs;
    context->m_failedRhs = rhs;
    return r;
  }
  if ((r = lhs->VpiType() - rhs->VpiType()) != 0) {
    context->m_failedLhs = lhs;
    context->m_failedRhs = rhs;
    return r;
  }
  if ((r = (lhs->VpiTopModule() == rhs->VpiTopModule()) ? 0 : (lhs->VpiTopModule() ? 1 : -1)) != 0) {
    context->m_failedLhs = lhs;
    context->m_failedRhs = rhs;
    return r;
  }
  if ((r = lhs->VpiDefDecayTime() - rhs->VpiDefDecayTime()) != 0) {
    context->m_failedLhs = lhs;
    context->m_failedRhs = rhs;
    return r;
  }
  if ((r = SafeCompare(lhs->Global_clocking(), rhs->Global_clocking(), context)) != 0) return r;
  if ((r = SafeCompare(lhs->Default_clocking(), rhs->Default_clocking(), context)) != 0) return r;
  if ((r = SafeCompare(lhs->Expr_dist(), rhs->Expr_dist(), context)) != 0) return r;
  if ((r = SafeCompare(lhs->Module_array(), rhs->Module_array(), context)) != 0) return r;
  if ((r = SafeCompare(lhs->Instance_array(), rhs->Instance_array(), context)) != 0) return r;
  if ((r = SafeCompare(lhs, lhs->Ports(), rhs, rhs->Ports(), context)) != 0) return r;
  if ((r = SafeCompare(lhs, lhs->Interfaces(), rhs, rhs->Interfaces(), context)) != 0) return r;
  if ((r = SafeCompare(lhs, lhs->Interface_arrays(), rhs, rhs->Interface_arrays(), context)) != 0) return r;
  if ((r = SafeCompare(lhs, lhs->Process(), rhs, rhs->Process(), context)) != 0) return r;
  if ((r = SafeCompare(lhs, lhs->Modules(), rhs, rhs->Modules(), context)) != 0) return r;
  if ((r = SafeCompare(lhs, lhs->Module_arrays(), rhs, rhs->Module_arrays(), context)) != 0) return r;
  if ((r = SafeCompare(lhs, lhs->Gen_scope_arrays(), rhs, rhs->Gen_scope_arrays(), context)) != 0) return r;
  if ((r = SafeCompare(lhs, lhs->Cont_assigns(), rhs, rhs->Cont_assigns(), context)) != 0) return r;
  if ((r = SafeCompare(lhs, lhs->Primitives(), rhs, rhs->Primitives(), context)) != 0) return r;
  if ((r = SafeCompare(lhs, lhs->Primitive_arrays(), rhs, rhs->Primitive_arrays(), context)) != 0) return r;
  if ((r = SafeCompare(lhs, lhs->Mod_paths(), rhs, rhs->Mod_paths(), context)) != 0) return r;
  if ((r = SafeCompare(lhs, lhs->Tchks(), rhs, rhs->Tchks(), context)) != 0) return r;
  if ((r = SafeCompare(lhs, lhs->Def_params(), rhs, rhs->Def_params(), context)) != 0) return r;
  if ((r = SafeCompare(lhs, lhs->Io_decls(), rhs, rhs->Io_decls(), context)) != 0) return r;
  if ((r = SafeCompare(lhs, lhs->Alias_stmts(), rhs, rhs->Alias_stmts(), context)) != 0) return r;
  if ((r = SafeCompare(lhs, lhs->Clocking_blocks(), rhs, rhs->Clocking_blocks(), context)) != 0) return r;
  if ((r = SafeCompare(lhs, lhs->Elab_tasks(), rhs, rhs->Elab_tasks(), context)) != 0) return r;
  if ((r = SafeCompare(lhs, lhs->Ref_modules(), rhs, rhs->Ref_modules(), context)) != 0) return r;
  if ((r = SafeCompare(lhs, lhs->Gen_stmts(), rhs, rhs->Gen_stmts(), context)) != 0) return r;
  if ((r = lhs->VpiEndLabel().compare(rhs->VpiEndLabel())) != 0) {
    context->m_failedLhs = lhs;
    context->m_failedRhs = rhs;
    return r;
  }
  return r;
}

