const BaseClass* real_typespec::GetByVpiName(std::string_view name) const {
  if ((resolution_func_ != nullptr) && (resolution_func_->VpiName().compare(name) == 0)) {
    return resolution_func_;
  }
  return basetype_t::GetByVpiName(name);
}

std::tuple<const BaseClass*, UHDM_OBJECT_TYPE, const std::vector<const BaseClass*>*> real_typespec::GetByVpiType(int32_t type) const {
  switch (type) {
    case vpiFunction: return std::make_tuple(resolution_func_, static_cast<UHDM_OBJECT_TYPE>(0), nullptr);
  }
  return basetype_t::GetByVpiType(type);
}

real_typespec::vpi_property_value_t real_typespec::GetVpiPropertyValue(int32_t property) const {
  switch (property) {
    case vpiType: return vpi_property_value_t(VpiType());
  }
  return basetype_t::GetVpiPropertyValue(property);
}

void real_typespec::DeepCopy(real_typespec* clone, BaseClass* parent, CloneContext* context) const {
  [[maybe_unused]] ElaboratorContext* const elaboratorContext = clonecontext_cast<ElaboratorContext*>(context);
  elaboratorContext->m_elaborator.enterReal_typespec(clone, nullptr);
  basetype_t::DeepCopy(clone, parent, context);
  if (auto obj = Resolution_func()) clone->Resolution_func(obj->DeepClone(clone, context));
  elaboratorContext->m_elaborator.leaveReal_typespec(clone, nullptr);
}

real_typespec* real_typespec::DeepClone(BaseClass* parent, CloneContext* context) const {
  real_typespec* const clone = context->m_serializer->MakeReal_typespec();
  const uint32_t id = clone->UhdmId();
  *clone = *this;
  clone->UhdmId(id);
  DeepCopy(clone, parent, context);
  return clone;
}

int32_t real_typespec::Compare(const BaseClass *other, CompareContext* context) const {
  int32_t r = 0;
  if (!context->m_visited.insert(this).second) return r;
  if ((r = basetype_t::Compare(other, context)) != 0) return r;

  const thistype_t *const lhs = this;
  const thistype_t *const rhs = (const thistype_t *)other;

  if ((r = SafeCompare(lhs->Resolution_func(), rhs->Resolution_func(), context)) != 0) return r;
  return r;
}

