#include <uhdm/reg_array.h>
#include <uhdm/ElaboratorListener.h>
#include <uhdm/ElaboratorListener.h>
#include <uhdm/Serializer.h>
#include <uhdm/expr.h>
#include <uhdm/reg.h>


namespace UHDM {
const BaseClass* reg_array::GetByVpiName(std::string_view name) const {
  if ((left_expr_ != nullptr) && (left_expr_->VpiName().compare(name) == 0)) {
    return left_expr_;
  }
  if ((right_expr_ != nullptr) && (right_expr_->VpiName().compare(name) == 0)) {
    return right_expr_;
  }
  if (regs_ != nullptr) {
    for (const BaseClass *ref : *regs_) {
      if (ref->VpiName().compare(name) == 0) return ref;
    }
  }
  return basetype_t::GetByVpiName(name);
}

std::tuple<const BaseClass*, UHDM_OBJECT_TYPE, const std::vector<const BaseClass*>*> reg_array::GetByVpiType(int32_t type) const {
  switch (type) {
    case vpiLeftRange: return std::make_tuple(left_expr_, static_cast<UHDM_OBJECT_TYPE>(0), nullptr);
    case vpiMemoryWord: return std::make_tuple(nullptr, uhdmregs, (const std::vector<const BaseClass*>*)regs_);
    case vpiRightRange: return std::make_tuple(right_expr_, static_cast<UHDM_OBJECT_TYPE>(0), nullptr);
  }
  return basetype_t::GetByVpiType(type);
}

reg_array::vpi_property_value_t reg_array::GetVpiPropertyValue(int32_t property) const {
  switch (property) {
    case vpiIsMemory: return vpi_property_value_t(VpiIsMemory());
    case vpiType: return vpi_property_value_t(VpiType());
  }
  return basetype_t::GetVpiPropertyValue(property);
}

void reg_array::DeepCopy(reg_array* clone, BaseClass* parent, CloneContext* context) const {
  [[maybe_unused]] ElaboratorContext* const elaboratorContext = clonecontext_cast<ElaboratorContext*>(context);
  elaboratorContext->m_elaborator.enterReg_array(clone, nullptr);
  basetype_t::DeepCopy(clone, parent, context);
  if (auto obj = Left_expr()) clone->Left_expr(obj->DeepClone(clone, context));
  if (auto obj = Right_expr()) clone->Right_expr(obj->DeepClone(clone, context));
  if (auto vec = Regs()) {
    auto clone_vec = context->m_serializer->MakeRegVec();
    clone->Regs(clone_vec);
    for (auto obj : *vec) {
      clone_vec->push_back(obj->DeepClone(clone, context));
    }
  }
  elaboratorContext->m_elaborator.leaveReg_array(clone, nullptr);
}

reg_array* reg_array::DeepClone(BaseClass* parent, CloneContext* context) const {
  reg_array* const clone = context->m_serializer->MakeReg_array();
  const uint32_t id = clone->UhdmId();
  *clone = *this;
  clone->UhdmId(id);
  DeepCopy(clone, parent, context);
  return clone;
}

int32_t reg_array::Compare(const BaseClass *other, CompareContext* context) const {
  int32_t r = 0;
  if (!context->m_visited.insert(this).second) return r;
  if ((r = basetype_t::Compare(other, context)) != 0) return r;

  const thistype_t *const lhs = this;
  const thistype_t *const rhs = (const thistype_t *)other;

  if ((r = (lhs->VpiIsMemory() == rhs->VpiIsMemory()) ? 0 : (lhs->VpiIsMemory() ? 1 : -1)) != 0) {
    context->m_failedLhs = lhs;
    context->m_failedRhs = rhs;
    return r;
  }
  if ((r = SafeCompare(lhs->Left_expr(), rhs->Left_expr(), context)) != 0) return r;
  if ((r = SafeCompare(lhs->Right_expr(), rhs->Right_expr(), context)) != 0) return r;
  if ((r = SafeCompare(lhs, lhs->Regs(), rhs, rhs->Regs(), context)) != 0) return r;
  return r;
}

}  // namespace UHDM
